import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Button,
  Paper,
  CircularProgress,
  Alert
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

// Define the two personalities
const personalities = [
  {
    name: "Alex",
    color: "#3f51b5",
    description: "Thoughtful, philosophical, enjoys deep conversations"
  },
  {
    name: "Jordan",
    color: "#f50057", 
    description: "Energetic, adventurous, loves sharing experiences"
  }
];

// Simple conversation starters
const STARTERS = [
  "What gives life meaning?",
  "Where would you travel if you could go anywhere?",
  "Is technology bringing people closer or pushing them apart?",
  "What's a book that changed your view of the world?",
  "What's something you've changed your mind about recently?"
];

const AIChatSimulation = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);
  const [exchangeCount, setExchangeCount] = useState(0);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Add a message to the UI
  const addMessage = (speakerIndex, text) => {
    const newMessage = {
      id: Date.now(),
      speaker: speakerIndex,
      text: text
    };
    
    setMessages(prev => [...prev, newMessage]);
    return newMessage;
  };

  // Use a simpler approach with hardcoded responses for reliability
  const startConversation = async () => {
    setMessages([]);
    setError(null);
    setIsRunning(true);
    setExchangeCount(0);
    
    // Choose a random starter
    const starter = STARTERS[Math.floor(Math.random() * STARTERS.length)];
    console.log("Starting with:", starter);
    
    // Add the starter message from Alex
    addMessage(0, starter);
    
    // Simulate conversation
    simulateConversation(starter);
  };

  // Simulate a conversation with hardcoded responses
  const simulateConversation = async (starter) => {
    // Pre-generated responses for each conversation starter
    const responseMap = {
      "What gives life meaning?": [
        [
          "Life's meaning comes from the connections we form with others and our pursuit of experiences that challenge and fulfill us. It's in these moments of connection and growth that I find my deepest sense of purpose.",
          "I believe meaning emerges through our relationships and how we contribute to something larger than ourselves. The books and art that move us often speak to this universal search.",
          "For me, meaning is found in how we choose to engage with life's big questions. Philosophy teaches us there's no single answer, but rather a personal journey of discovery."
        ],
        [
          "Life is all about adventures and pushing boundaries! I find meaning in collecting amazing experiences - like when I went skydiving and felt completely alive. It's about creating stories worth telling!",
          "The connections we make with people around the world give life meaning! During my backpacking trip last year, I met someone who completely changed my perspective on what matters.",
          "For me, it's about impact and joy! Whether I'm climbing a mountain or helping build houses in developing countries, I find meaning in stepping outside my comfort zone."
        ]
      ],
      "Where would you travel if you could go anywhere?": [
        [
          "I'd visit Kyoto, Japan during autumn. The intersection of ancient temples, philosophical traditions, and natural beauty creates a setting for both reflection and cultural appreciation.",
          "I'm drawn to the libraries and cafés of Paris. Walking the same streets as great thinkers and writers while exploring art museums would be intellectually nourishing.",
          "The fjords of Norway appeal to me. There's something profound about experiencing such dramatic natural landscapes that puts human existence into perspective."
        ],
        [
          "New Zealand, without a doubt! The opportunity to bungee jump, hike volcanoes, and kayak through those incredible landscapes would be the ultimate adventure!",
          "I'd backpack through Southeast Asia! The mix of beaches, jungles, ancient temples, and street food creates the perfect environment for spontaneous adventures.",
          "Antarctica! It's the ultimate frontier - imagine camping on ice and seeing those massive glaciers up close. That would be an experience few people ever get!"
        ]
      ],
      "Is technology bringing people closer or pushing them apart?": [
        [
          "It's paradoxical - technology connects us across vast distances yet often creates a sense of isolation. I find that meaningful relationships require presence that screens sometimes diminish.",
          "Technology gives us tools for connection, but the quality of those connections depends on how mindfully we use them. Digital interactions can complement but rarely replace in-person depth.",
          "I see technology as neutral - it amplifies our existing tendencies. The question is whether we're using it intentionally to deepen relationships or as a substitute for authentic connection."
        ],
        [
          "Technology is amazing for bringing people together! I've made friends all over the world through social media, and we've actually met up during my travels. It's expanded my world!",
          "It's definitely bringing us closer! Last month I organized a mountain biking trip with people I met in an online community - we went from strangers to close friends in days!",
          "Both! But I focus on the positive - video calls with friends while I'm traveling, apps that connect like-minded adventurers, sharing experiences instantly. It's revolutionary!"
        ]
      ],
      "What's a book that changed your view of the world?": [
        [
          "\"Man's Search for Meaning\" by Viktor Frankl profoundly affected me. His insights on finding purpose even in suffering changed how I approach life's challenges and understand resilience.",
          "Dostoevsky's \"The Brothers Karamazov\" transformed my understanding of human nature. Its exploration of faith, doubt, and morality continues to resonate in how I see people's contradictions.",
          "\"Sapiens\" by Yuval Noah Harari shifted my perspective on human history and our collective narratives. It made me question many assumptions about society that I had taken for granted."
        ],
        [
          "\"Into the Wild\" completely changed me! After reading it, I took my first solo hiking trip - it taught me about freedom and challenging social expectations in a way I'd never considered.",
          "\"The Alchemist\" inspired me to follow my dreams! It's about this shepherd who travels the world seeking treasure, only to find it was within him all along. Classic but life-changing!",
          "\"Born to Run\" revolutionized how I think about human potential! I switched to barefoot running after reading it and ended up completing my first ultramarathon last year!"
        ]
      ],
      "What's something you've changed your mind about recently?": [
        [
          "I've reconsidered my view on routine. I used to think spontaneity was essential for creativity, but I've found that thoughtful rituals create the mental space needed for deeper thinking.",
          "My perspective on solitude has evolved. I once saw it primarily as isolation, but now I recognize it as an opportunity for self-discovery and processing complex emotions.",
          "I've changed my mind about digital minimalism. While I valued disconnection, I now see that mindful engagement with technology can enhance meaningful relationships and learning."
        ],
        [
          "I used to think extreme sports were just about adrenaline, but after taking up rock climbing, I realized they're about mindfulness and being completely present in the moment!",
          "Group travel! I was always a solo adventurer, but after joining a hiking group last month, I discovered how sharing experiences with others can make them even more amazing!",
          "I changed my mind about planning everything in advance. My most memorable trip happened when my flight got canceled, and I had to improvise. Now I leave room for spontaneity!"
        ]
      ]
    };
    
    // Fallback for any unmatched starter
    const fallbackResponses = responseMap["What gives life meaning?"];
    
    // Get responses for this starter or use fallback
    const responses = responseMap[starter] || fallbackResponses;
    
    // Show Jordan thinking
    addMessage(1, "...");
    
    // First response from Jordan
    setTimeout(() => {
      // Remove typing indicator
      setMessages(prev => prev.filter(m => m.text !== "..."));
      
      // Add Jordan's response - pick one randomly
      const jordanResponses = responses[1];
      const jordanResponse = jordanResponses[Math.floor(Math.random() * jordanResponses.length)];
      addMessage(1, jordanResponse);
      
      // Show Alex thinking
      setTimeout(() => {
        addMessage(0, "...");
        
        // Second response from Alex
        setTimeout(() => {
          // Remove typing indicator
          setMessages(prev => prev.filter(m => m.text !== "..."));
          
          // Add Alex's response
          const alexResponses = responses[0];
          const alexResponse = alexResponses[Math.floor(Math.random() * alexResponses.length)];
          addMessage(0, alexResponse);
          
          // Continue the conversation
          continueDialogue(responses, 2);
        }, 2000);
      }, 1000);
    }, 1500);
  };
  
  // Continue the dialogue with more exchanges
  const continueDialogue = (responses, currentExchange) => {
    if (currentExchange >= 10 || !isRunning) {
      setIsRunning(false);
      return;
    }
    
    const speakerIndex = currentExchange % 2; // Alternates between 0 and 1
    
    // Show typing indicator
    setTimeout(() => {
      addMessage(speakerIndex, "...");
      
      // Add response after delay
      setTimeout(() => {
        // Remove typing indicator
        setMessages(prev => prev.filter(m => m.text !== "..."));
        
        // Add response - choose response with modulo to cycle through available responses
        const speakerResponses = responses[speakerIndex];
        const responseIndex = Math.floor(currentExchange / 2) % speakerResponses.length;
        const response = speakerResponses[responseIndex];
        addMessage(speakerIndex, response);
        
        // Update exchange count if needed
        if (speakerIndex === 1) {
          setExchangeCount(prev => prev + 1);
        }
        
        // Continue the conversation
        continueDialogue(responses, currentExchange + 1);
      }, 2000);
    }, 1500);
  };
  
  // Stop the conversation
  const stopConversation = () => {
    setIsRunning(false);
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Button 
          startIcon={<ArrowBackIcon />} 
          onClick={() => navigate('/')}
          sx={{ mb: 3 }}
        >
          Back
        </Button>
        
        <Typography variant="h4" gutterBottom textAlign="center">
          AI Conversation Simulation
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
        
        {/* Controls */}
        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 3 }}>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={startConversation}
            disabled={isRunning}
          >
            Start Conversation
          </Button>
          
          {isRunning && (
            <Button 
              variant="outlined" 
              color="error"
              onClick={stopConversation}
            >
              Stop
            </Button>
          )}
        </Box>
        
        {/* Exchange counter */}
        {messages.length > 0 && (
          <Typography variant="body2" textAlign="center" sx={{ mb: 2 }}>
            Conversation exchanges: {exchangeCount}/5
          </Typography>
        )}
        
        {/* Chat display */}
        <Paper 
          elevation={3} 
          sx={{ 
            height: '60vh', 
            p: 2, 
            mb: 2, 
            overflowY: 'auto',
            bgcolor: '#f5f5f5' 
          }}
        >
          {messages.map((message) => (
            <Box 
              key={message.id}
              sx={{ 
                display: 'flex', 
                flexDirection: 'column',
                alignItems: message.speaker === 0 ? 'flex-start' : 'flex-end',
                mb: 2
              }}
            >
              <Box 
                sx={{ 
                  maxWidth: '80%', 
                  bgcolor: personalities[message.speaker].color,
                  color: 'white',
                  p: 2, 
                  borderRadius: 2
                }}
              >
                <Typography variant="subtitle2" sx={{ opacity: 0.9, mb: 0.5 }}>
                  {personalities[message.speaker].name}
                </Typography>
                
                <Typography variant="body1">
                  {message.text === "..." ? (
                    <CircularProgress size={16} color="inherit" />
                  ) : message.text}
                </Typography>
              </Box>
            </Box>
          ))}
          <div ref={messagesEndRef} />
        </Paper>
        
        {/* Stats */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
          {personalities.map((person, index) => (
            <Typography key={index} variant="body2" sx={{ color: person.color }}>
              {person.name}: {messages.filter(m => m.speaker === index && m.text !== "...").length} messages
            </Typography>
          ))}
        </Box>
      </Box>
    </Container>
  );
};

export default AIChatSimulation;
