import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  Avatar,
  Button,
  CircularProgress,
  Divider,
  Alert
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import Groq from 'groq-sdk';

// Type declaration for TypeScript
declare const process: any;

// Mock Groq client for development
const groq = new Groq({
  apiKey: process.env.REACT_APP_GROQ_API_KEY || '',
  dangerouslyAllowBrowser: true
});

// Define personality types
const personalities = [
  {
    name: "Alex",
    avatar: "A",
    color: "#3f51b5",
    systemPrompt: "You are Alex, a thoughtful and creative person who loves art, literature, and philosophical discussions. You are introspective, kind, and curious about the world. You enjoy deep conversations and asking thought-provoking questions. You speak in a warm, articulate manner and occasionally reference books or art you enjoy. Keep your responses under 100 words."
  },
  {
    name: "Jordan",
    avatar: "J",
    color: "#f50057",
    systemPrompt: "You are Jordan, an energetic and adventurous person who loves outdoor activities, travel, and trying new experiences. You're outgoing, enthusiastic, and have a good sense of humor. You speak in a lively, expressive way and often share stories of your adventures or things you'd like to try. You're optimistic and encouraging of others. Keep your responses under 100 words."
  }
];

// Sample conversation starters
const conversationStarters = [
  "What do you think gives life meaning?",
  "If you could travel anywhere, where would you go and why?",
  "What's something you've changed your mind about recently?",
  "Do you think technology is bringing people closer together or pushing them apart?",
  "What's a book or movie that changed how you see the world?"
];

const AIChatSimulation = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState<{sender: number, text: string}[]>([]);
  const [isSimulating, setIsSimulating] = useState(false);
  const [conversationStarted, setConversationStarted] = useState(false);
  const [messageCount, setMessageCount] = useState({ 0: 0, 1: 0 });
  const [error, setError] = useState<string | null>(null);
  const [isApiReady, setIsApiReady] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  
  // Function to generate a response using Groq API
  const generateResponse = async (personalityIndex: number, context: string[]) => {
    const personality = personalities[personalityIndex];
    console.log(`Generating response for ${personality.name} with context:`, context);
    
    // For debugging purposes, let's create a hardcoded response first to test the flow
    const hardcodedResponses = {
      0: [
        "I find meaning in connection and creative expression. Art and literature allow us to explore the depths of human experience in ways that resonate beyond words.",
        "What a fascinating question. I believe meaning emerges from our relationships and how we contribute to something larger than ourselves."
      ],
      1: [
        "Life is all about experiences! I find meaning in pushing boundaries, exploring new places, and connecting with people from all walks of life.",
        "For me, meaning comes from adventure and growth. Every challenge I overcome adds another chapter to my story!"
      ]
    };
    
    // Return a hardcoded response first to debug the UI flow
    console.log("Using hardcoded response for now to debug UI flow");
    return hardcodedResponses[personalityIndex][Math.floor(Math.random() * 2)];
    
    // Once UI flow is working, uncomment this section to use the actual API
    /*
    try {
      // Format the conversation history for context
      const conversationHistory = context.map((msg, i) => {
        const role = i % 2 === 0 ? 
          (personalityIndex === 0 ? personalities[1].name : personalities[0].name) : 
          personality.name;
        return `${role}: ${msg}`;
      }).join('\n');
      
      // Make the API call to Groq
      const completion = await groq.chat.completions.create({
        messages: [
          {
            role: "system",
            content: personality.systemPrompt
          },
          {
            role: "user",
            content: `You are having a conversation with ${personalityIndex === 0 ? personalities[1].name : personalities[0].name}. Here is the conversation so far:\n\n${conversationHistory}\n\nRespond to the last message as ${personality.name}. Keep your response under 80 words and stay in character.`
          }
        ],
        model: "llama-3-8b-8192",
        temperature: 0.7,
        max_tokens: 200,
        top_p: 1,
      });
      
      // Extract and validate response
      const responseText = completion.choices[0]?.message?.content;
      if (!responseText || responseText.trim() === '') {
        throw new Error("Empty response received");
      }
      
      return responseText;
    } catch (err) {
      console.error("Error generating response:", err);
      
      // More robust fallback based on personality
      const fallbacks = personalityIndex === 0 ? [
        "I find this conversation fascinating. The way we connect through ideas is what gives meaning to our interactions.",
        "That's an intriguing perspective. It reminds me of something I read recently about human connection.",
        "I've been reflecting on what you said. There's a deeper truth there that resonates with me."
      ] : [
        "That's awesome! I had a similar experience when I was hiking in the mountains last summer.",
        "I totally get what you mean! Life is all about seizing opportunities and making memories.",
        "That's such an interesting take! I'm always looking for new adventures that challenge my perspective."
      ];
      
      return fallbacks[Math.floor(Math.random() * fallbacks.length)];
    }
    */
  };
  
  // Scroll to bottom of message container
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);
  
  // Verify API key is present
  useEffect(() => {
    if (!process.env.REACT_APP_GROQ_API_KEY) {
      setIsApiReady(false);
      setError("Groq API key not found. Using pre-written responses instead.");
    }
  }, []);
  
  // Add this after checking if API key exists
  useEffect(() => {
    // Log API status without revealing the key
    console.log("API Key present:", !!process.env.REACT_APP_GROQ_API_KEY);
    
    // Try to verify API access at component mount
    const verifyApiAccess = async () => {
      try {
        // Test API with a simple prompt
        await groq.chat.completions.create({
          messages: [
            { role: "user", content: "Hello" }
          ],
          model: "llama-3-8b-8192",
          max_tokens: 10,
        });
        setIsApiReady(true);
      } catch (err) {
        console.error("Failed to connect to Groq API:", err);
        setIsApiReady(false);
        setError("Could not connect to Groq API. Please check your API key.");
      }
    };
    
    if (process.env.REACT_APP_GROQ_API_KEY) {
      verifyApiAccess();
    }
  }, []);
  
  // Start the conversation
  const startConversation = () => {
    console.log("Starting conversation...");
    setIsSimulating(true);
    setConversationStarted(true);
    setMessages([]);
    setMessageCount({ 0: 0, 1: 0 });
    setError(null);
    
    // Start with a random conversation opener from personality 0
    const starter = conversationStarters[Math.floor(Math.random() * conversationStarters.length)];
    console.log("Initial message:", starter);
    
    // Directly add the first message - this was working before
    setMessages([{ sender: 0, text: starter }]);
    setMessageCount(prev => ({ ...prev, 0: 1 }));
    
    // Continue the conversation after a delay
    setTimeout(() => {
      console.log("Calling continueConversation with sender 0");
      continueConversation(0);
    }, 300);
  };
  
  // Function to continue the conversation
  const continueConversation = async (lastSender: number) => {
    console.log("⏩ Continuing conversation, last sender:", lastSender);
    
    if (!isSimulating) {
      console.log("❌ Not simulating, returning");
      return;
    }
    
    try {
      // Determine next sender
      const nextSender = lastSender === 0 ? 1 : 0;
      console.log(`🎭 Next sender will be: ${personalities[nextSender].name}`);
      
      // Check if we've reached message limit
      if (messageCount[nextSender] >= 25) {
        console.log("🛑 Message limit reached, stopping simulation");
        setIsSimulating(false);
        return;
      }
      
      // Get conversation context
      const context = messages.map(m => m.text);
      console.log("💬 Context for next message:", context);
      
      // Show typing indicator
      console.log("⌨️ Adding typing indicator");
      
      // Use a simpler state update approach
      const updatedMessages = [...messages, { sender: nextSender, text: "..." }];
      setMessages(updatedMessages);
      console.log("📝 Added typing indicator, messages now:", updatedMessages);
      
      // Force UI update before proceeding
      await new Promise(resolve => setTimeout(resolve, 50));
      
      // Get response
      console.log("🤖 Getting AI response");
      const response = await generateResponse(nextSender, context);
      console.log("✅ Got response:", response);
      
      // Create final message list with real response
      const finalMessages = [...messages, { sender: nextSender, text: response }];
      console.log("📝 Setting final messages:", finalMessages);
      
      // Update state directly instead of manipulating previous state
      setMessages(finalMessages);
      
      // Update count separately
      const newCounts = { ...messageCount };
      newCounts[nextSender] += 1;
      console.log("🔢 Updating message count to:", newCounts);
      setMessageCount(newCounts);
      
      // Force UI update again
      await new Promise(resolve => setTimeout(resolve, 50));
      
      // Continue the conversation with a longer delay
      console.log("⏱️ Scheduling next message");
      setTimeout(() => {
        continueConversation(nextSender);
      }, 1000); // Longer delay for readability
    } catch (err) {
      console.error("💥 Fatal error in conversation flow:", err);
      setError("Conversation error: " + (err.message || "Unknown error"));
      setIsSimulating(false);
    }
  };
  
  // Make sure we're catching and displaying all errors
  useEffect(() => {
    // Add a global error handler
    const handleError = (error) => {
      console.error("Global error:", error);
      setError("An error occurred: " + error.message);
      setIsSimulating(false);
    };
    
    window.addEventListener('error', handleError);
    return () => window.removeEventListener('error', handleError);
  }, []);
  
  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Button 
          startIcon={<ArrowBackIcon />} 
          onClick={() => navigate('/')}
          sx={{ mb: 3 }}
        >
          Back to Home
        </Button>
        
        <Typography variant="h4" gutterBottom textAlign="center">
          AI Match Simulation
        </Typography>
        
        <Typography variant="body1" color="text.secondary" paragraph textAlign="center">
          Watch as two AI personalities have a conversation to see how our matching system works. 
          Each AI has a distinct personality and communication style.
        </Typography>
        
        {error && (
          <Alert severity="warning" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
        
        {/* Start button */}
        {!conversationStarted && (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <Button
              variant="contained"
              color="primary"
              size="large"
              startIcon={<PlayArrowIcon />}
              onClick={() => {
                // Just call startConversation directly - don't add test messages
                startConversation();
              }}
              sx={{ py: 2, px: 4 }}
            >
              Start AI Conversation
            </Button>
          </Box>
        )}
        
        {/* Chat header with profiles */}
        {conversationStarted && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2, mt: 4 }}>
              {personalities.map((personality, index) => (
                <Box key={index} sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ bgcolor: personality.color, mr: 1 }}>
                    {personality.avatar}
                  </Avatar>
                  <Typography variant="subtitle1">
                    {personality.name}
                  </Typography>
                </Box>
              ))}
            </Box>
            
            {/* Chat container */}
            <Paper 
              elevation={3} 
              sx={{ 
                height: '60vh', 
                overflowY: 'auto',
                p: 2,
                backgroundColor: '#f5f5f5'
              }}
            >
              {messages.map((message, index) => (
                <Box 
                  key={index}
                  sx={{
                    display: 'flex',
                    justifyContent: message.sender === 0 ? 'flex-start' : 'flex-end',
                    mb: 2
                  }}
                >
                  <Box
                    sx={{
                      maxWidth: '70%',
                      bgcolor: message.sender === 0 ? personalities[0].color : personalities[1].color,
                      color: 'white',
                      p: 2,
                      borderRadius: 2
                    }}
                  >
                    <Typography variant="body1">
                      {message.text === "..." ? (
                        <CircularProgress size={16} color="inherit" />
                      ) : (
                        <>
                          <Typography variant="caption" sx={{ display: 'block', opacity: 0.8, mb: 0.5 }}>
                            {personalities[message.sender].name}
                          </Typography>
                          {message.text}
                        </>
                      )}
                    </Typography>
                  </Box>
                </Box>
              ))}
              <div ref={messagesEndRef} />
            </Paper>
            
            {/* Progress indicator */}
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
              <Typography variant="body2">
                {personalities[0].name}: {messageCount[0]}/25 messages
              </Typography>
              <Typography variant="body2">
                {personalities[1].name}: {messageCount[1]}/25 messages
              </Typography>
            </Box>
            
            {!isSimulating && conversationStarted && (
              <Box sx={{ mt: 3, textAlign: 'center' }}>
                <Typography variant="h6" gutterBottom>
                  Conversation Complete!
                </Typography>
                <Button 
                  variant="contained" 
                  color="primary"
                  onClick={() => navigate('/create-profile')}
                  sx={{ mt: 2, mr: 2 }}
                >
                  Create Your Profile
                </Button>
                <Button 
                  variant="outlined" 
                  color="secondary"
                  onClick={startConversation}
                  sx={{ mt: 2 }}
                >
                  Start New Conversation
                </Button>
              </Box>
            )}
          </>
        )}
      </Box>
    </Container>
  );
};

export default AIChatSimulation;
