import React from 'react';
import { Box } from '@mui/material';

const BackgroundDecoration: React.FC = () => {
  return (
    <>
      {/* Top right corner decoration */}
      <Box
        sx={{
          position: 'fixed',
          top: 0,
          right: 0,
          width: { xs: '150px', md: '250px' },
          height: { xs: '150px', md: '250px' },
          backgroundImage: 'url("https://source.unsplash.com/random/400x400/?abstract,colorful,3d")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          opacity: 0.6,
          zIndex: -1,
          borderBottomLeftRadius: '100%',
          pointerEvents: 'none', // Ensures it doesn't interfere with clicks
        }}
      />

      {/* Bottom left corner decoration */}
      <Box
        sx={{
          position: 'fixed',
          bottom: 0,
          left: 0,
          width: { xs: '150px', md: '250px' },
          height: { xs: '150px', md: '250px' },
          backgroundImage: 'url("https://source.unsplash.com/random/400x400/?abstract,colorful,3d,geometric")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          opacity: 0.6,
          zIndex: -1,
          borderTopRightRadius: '100%',
          pointerEvents: 'none', // Ensures it doesn't interfere with clicks
        }}
      />
    </>
  );
};

export default BackgroundDecoration;
