import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Button,
  Paper,
  CircularProgress,
  Alert
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

// Simple configuration
const GROQ_API_KEY = process.env.REACT_APP_GROQ_API_KEY;
const API_ENDPOINT = "https://api.groq.com/openai/v1/chat/completions";
const MODEL = "llama3-70b-8192";

// Define the two personalities
const personalities = [
  {
    name: "Alex",
    color: "#3f51b5",
    description: "Thoughtful, philosophical, enjoys deep conversations"
  },
  {
    name: "Jordan",
    color: "#f50057", 
    description: "Energetic, adventurous, loves sharing experiences"
  }
];

// Simple conversation starters
const STARTERS = [
  "What gives life meaning?",
  "Where would you travel if you could go anywhere?",
  "Is technology bringing people closer or pushing them apart?",
  "What's a book that changed your view of the world?",
  "What's something you've changed your mind about recently?"
];

const AIChatSimulation = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Start the conversation
  const startConversation = async () => {
    setMessages([]);
    setError(null);
    setIsRunning(true);
    
    // Choose a random starter
    const starter = STARTERS[Math.floor(Math.random() * STARTERS.length)];
    
    // Add the starter message from Alex
    addMessage(0, starter);
    
    // Start the conversation loop
    try {
      // First response comes from Jordan (personality 1)
      await getNextResponse(0, starter);
    } catch (err) {
      setError(`Failed to start conversation: ${err.message}`);
      setIsRunning(false);
    }
  };

  // Add a message to the conversation
  const addMessage = (speakerIndex, text) => {
    const newMessage = {
      id: Date.now(),
      speaker: speakerIndex,
      text: text
    };
    
    setMessages(prev => [...prev, newMessage]);
    return newMessage;
  };

  // Get response from the API
  const getNextResponse = async (lastSpeaker, lastMessage) => {
    if (!isRunning) return;
    
    // Toggle between speakers (0 and 1)
    const currentSpeaker = lastSpeaker === 0 ? 1 : 0;
    
    // Show loading state
    setIsLoading(true);
    addMessage(currentSpeaker, "...");
    
    try {
      // Create the prompt based on personality
      const prompt = createPrompt(currentSpeaker, lastSpeaker, lastMessage);
      
      // Call the API
      const response = await fetch(API_ENDPOINT, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${GROQ_API_KEY}`
        },
        body: JSON.stringify({
          model: MODEL,
          messages: [{ role: "user", content: prompt }],
          max_tokens: 150,
          temperature: 0.7
        })
      });
      
      // Handle errors
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`API error (${response.status}): ${errorText}`);
      }
      
      // Process the response
      const data = await response.json();
      const responseText = data.choices?.[0]?.message?.content?.trim();
      
      if (!responseText) {
        throw new Error("Empty response from API");
      }
      
      // Replace loading message with actual response
      setMessages(prev => {
        const newMessages = [...prev];
        // Replace the last message (loading indicator)
        newMessages[newMessages.length - 1] = {
          id: Date.now(),
          speaker: currentSpeaker,
          text: responseText
        };
        return newMessages;
      });
      
      // Continue the conversation after a delay
      setTimeout(() => {
        if (isRunning && messages.length < 20) {
          getNextResponse(currentSpeaker, responseText);
        } else {
          setIsRunning(false);
        }
      }, 2000);
      
    } catch (err) {
      console.error("API error:", err);
      
      // Use a fallback response
      const fallback = currentSpeaker === 0 ? 
        "I find that perspective quite thought-provoking. It reminds me of some philosophical ideas I've been exploring lately." : 
        "That's amazing! I had a similar experience during my travels last year. It really changed my outlook!";
      
      // Replace loading message with fallback
      setMessages(prev => {
        const newMessages = [...prev];
        newMessages[newMessages.length - 1] = {
          id: Date.now(),
          speaker: currentSpeaker,
          text: fallback
        };
        return newMessages;
      });
      
      // Continue with fallback
      setTimeout(() => {
        if (isRunning && messages.length < 20) {
          getNextResponse(currentSpeaker, fallback);
        } else {
          setIsRunning(false);
        }
      }, 2000);
      
      setError(`API error: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // Create a prompt for the API based on personality
  const createPrompt = (currentSpeaker, lastSpeaker, lastMessage) => {
    const current = personalities[currentSpeaker];
    const last = personalities[lastSpeaker];
    
    return `You are ${current.name} who is ${current.description}.
    
    ${last.name} just said to you: "${lastMessage}"
    
    Respond as ${current.name}, keeping your response under 80 words and staying in character.
    Be concise but authentic to your personality.`;
  };

  // Stop the conversation
  const stopConversation = () => {
    setIsRunning(false);
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Button 
          startIcon={<ArrowBackIcon />} 
          onClick={() => navigate('/')}
          sx={{ mb: 3 }}
        >
          Back
        </Button>
        
        <Typography variant="h4" gutterBottom textAlign="center">
          AI Conversation Simulation
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
        
        {/* Controls */}
        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 3 }}>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={startConversation}
            disabled={isRunning}
          >
            Start Conversation
          </Button>
          
          {isRunning && (
            <Button 
              variant="outlined" 
              color="secondary" 
              onClick={stopConversation}
            >
              Stop
            </Button>
          )}
        </Box>
        
        {/* Chat display */}
        <Paper 
          elevation={3} 
          sx={{ 
            height: '60vh', 
            p: 2, 
            mb: 2, 
            overflowY: 'auto',
            bgcolor: '#f5f5f5' 
          }}
        >
          {messages.map((message) => (
            <Box 
              key={message.id}
              sx={{ 
                display: 'flex', 
                flexDirection: 'column',
                alignItems: message.speaker === 0 ? 'flex-start' : 'flex-end',
                mb: 2
              }}
            >
              <Box 
                sx={{ 
                  maxWidth: '80%', 
                  bgcolor: personalities[message.speaker].color,
                  color: 'white',
                  p: 2, 
                  borderRadius: 2
                }}
              >
                <Typography variant="subtitle2" sx={{ opacity: 0.9, mb: 0.5 }}>
                  {personalities[message.speaker].name}
                </Typography>
                
                <Typography variant="body1">
                  {message.text === "..." ? (
                    <CircularProgress size={16} color="inherit" />
                  ) : message.text}
                </Typography>
              </Box>
            </Box>
          ))}
          <div ref={messagesEndRef} />
        </Paper>
        
        {/* Stats */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
          {personalities.map((person, index) => (
            <Typography key={index} variant="body2" sx={{ color: person.color }}>
              {person.name}: {messages.filter(m => m.speaker === index && m.text !== "...").length} messages
            </Typography>
          ))}
        </Box>
      </Box>
    </Container>
  );
};

export default AIChatSimulation;
