import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  Avatar,
  Button,
  CircularProgress,
  Divider,
  Alert
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import Groq from '@groq/groq-sdk';

// Initialize Groq client
// You'll need to add REACT_APP_GROQ_API_KEY to your .env file
const groq = new Groq({
  apiKey: process.env.REACT_APP_GROQ_API_KEY || '',
});

// Define personality types
const personalities = [
  {
    name: "Alex",
    avatar: "A",
    color: "#3f51b5",
    systemPrompt: "You are Alex, a thoughtful and creative person who loves art, literature, and philosophical discussions. You are introspective, kind, and curious about the world. You enjoy deep conversations and asking thought-provoking questions. You speak in a warm, articulate manner and occasionally reference books or art you enjoy. Keep your responses under 100 words."
  },
  {
    name: "Jordan",
    avatar: "J",
    color: "#f50057",
    systemPrompt: "You are Jordan, an energetic and adventurous person who loves outdoor activities, travel, and trying new experiences. You're outgoing, enthusiastic, and have a good sense of humor. You speak in a lively, expressive way and often share stories of your adventures or things you'd like to try. You're optimistic and encouraging of others. Keep your responses under 100 words."
  }
];

// Sample conversation starters
const conversationStarters = [
  "What do you think gives life meaning?",
  "If you could travel anywhere, where would you go and why?",
  "What's something you've changed your mind about recently?",
  "Do you think technology is bringing people closer together or pushing them apart?",
  "What's a book or movie that changed how you see the world?"
];

const AIChatSimulation = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState<{sender: number, text: string}[]>([]);
  const [isSimulating, setIsSimulating] = useState(false);
  const [conversationStarted, setConversationStarted] = useState(false);
  const [messageCount, setMessageCount] = useState({ 0: 0, 1: 0 });
  const [error, setError] = useState<string | null>(null);
  const [isApiReady, setIsApiReady] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  
  // Function to generate a response using Groq API
  const generateResponse = async (personalityIndex: number, context: string[]) => {
    const personality = personalities[personalityIndex];
    try {
      // Format the conversation history for context
      const conversationHistory = context.map((msg, i) => {
        const role = i % 2 === 0 ? 
          (personalityIndex === 0 ? personalities[1].name : personalities[0].name) : 
          personality.name;
        return `${role}: ${msg}`;
      }).join('\n');
      
      // Make the API call to Groq
      const completion = await groq.chat.completions.create({
        messages: [
          {
            role: "system",
            content: personality.systemPrompt
          },
          {
            role: "user",
            content: `You are having a conversation with ${personalityIndex === 0 ? personalities[1].name : personalities[0].name}. Here is the conversation so far:\n\n${conversationHistory}\n\nRespond to the last message as ${personality.name}. Keep your response under 80 words and stay in character.`
          }
        ],
        model: "llama-3-8b-8192",
        temperature: 0.7,
        max_tokens: 200,
      });
      
      return completion.choices[0]?.message?.content || "I'm not sure how to respond to that.";
    } catch (err) {
      console.error("Error generating response:", err);
      setError("Couldn't generate a response. Falling back to pre-written messages.");
      // Fallback to a simple response if API fails
      return `I'd love to continue our conversation about ${context[context.length - 1].split(' ').slice(0, 3).join(' ')}...`;
    }
  };
  
  // Scroll to bottom of message container
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);
  
  // Verify API key is present
  useEffect(() => {
    if (!process.env.REACT_APP_GROQ_API_KEY) {
      setIsApiReady(false);
      setError("Groq API key not found. Using pre-written responses instead.");
    }
  }, []);
  
  // Start the conversation
  const startConversation = () => {
    setIsSimulating(true);
    setConversationStarted(true);
    setMessages([]);
    setMessageCount({ 0: 0, 1: 0 });
    
    // Start with a random conversation opener from personality 0
    const starter = conversationStarters[Math.floor(Math.random() * conversationStarters.length)];
    setMessages([{ sender: 0, text: starter }]);
    setMessageCount({ ...messageCount, 0: 1 });
    
    // Continue the conversation after a delay
    setTimeout(() => {
      continueConversation(0);
    }, 1000);
  };
  
  // Function to continue the conversation
  const continueConversation = async (lastSender: number) => {
    if (!isSimulating) return;
    
    // Determine next sender
    const nextSender = lastSender === 0 ? 1 : 0;
    
    // Check if we've reached message limit
    if (messageCount[0] >= 25 || messageCount[1] >= 25) {
      setIsSimulating(false);
      return;
    }
    
    // Get conversation context (last 5 messages)
    const context = messages.slice(-5).map(m => m.text);
    
    try {
      // Show typing indicator
      setMessages(prev => [...prev, { sender: nextSender, text: "..." }]);
      
      // Generate response using Groq API
      const response = await generateResponse(nextSender, context);
      
      // Replace typing indicator with actual response
      setMessages(prev => [...prev.slice(0, -1), { sender: nextSender, text: response }]);
      setMessageCount(prev => ({ ...prev, [nextSender]: prev[nextSender] + 1 }));
      
      // Continue conversation after a delay
      setTimeout(() => {
        continueConversation(nextSender);
      }, 1000);
    } catch (err) {
      console.error("Error in conversation flow:", err);
      setError("There was an error in the conversation. Please try again.");
      setIsSimulating(false);
    }
  };
  
  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Button 
          startIcon={<ArrowBackIcon />} 
          onClick={() => navigate('/')}
          sx={{ mb: 3 }}
        >
          Back to Home
        </Button>
        
        <Typography variant="h4" gutterBottom textAlign="center">
          AI Match Simulation
        </Typography>
        
        <Typography variant="body1" color="text.secondary" paragraph textAlign="center">
          Watch as two AI personalities have a conversation to see how our matching system works. 
          Each AI has a distinct personality and communication style.
        </Typography>
        
        {error && (
          <Alert severity="warning" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
        
        {/* Start button */}
        {!conversationStarted && (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <Button
              variant="contained"
              color="primary"
              size="large"
              startIcon={<PlayArrowIcon />}
              onClick={startConversation}
              disabled={!isApiReady}
              sx={{ py: 2, px: 4 }}
            >
              Start AI Conversation
            </Button>
          </Box>
        )}
        
        {/* Chat header with profiles */}
        {conversationStarted && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2, mt: 4 }}>
              {personalities.map((personality, index) => (
                <Box key={index} sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ bgcolor: personality.color, mr: 1 }}>
                    {personality.avatar}
                  </Avatar>
                  <Typography variant="subtitle1">
                    {personality.name}
                  </Typography>
                </Box>
              ))}
            </Box>
            
            {/* Chat container */}
            <Paper 
              elevation={3} 
              sx={{ 
                height: '60vh', 
                overflowY: 'auto',
                p: 2,
                backgroundColor: '#f5f5f5'
              }}
            >
              {messages.map((message, index) => (
                <Box 
                  key={index}
                  sx={{
                    display: 'flex',
                    justifyContent: message.sender === 0 ? 'flex-start' : 'flex-end',
                    mb: 2
                  }}
                >
                  <Box
                    sx={{
                      maxWidth: '70%',
                      bgcolor: message.sender === 0 ? personalities[0].color : personalities[1].color,
                      color: 'white',
                      p: 2,
                      borderRadius: 2
                    }}
                  >
                    <Typography variant="body1">
                      {message.text === "..." ? (
                        <CircularProgress size={16} color="inherit" />
                      ) : (
                        message.text
                      )}
                    </Typography>
                  </Box>
                </Box>
              ))}
              <div ref={messagesEndRef} />
            </Paper>
            
            {/* Progress indicator */}
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
              <Typography variant="body2">
                {personalities[0].name}: {messageCount[0]}/25 messages
              </Typography>
              <Typography variant="body2">
                {personalities[1].name}: {messageCount[1]}/25 messages
              </Typography>
            </Box>
            
            {!isSimulating && conversationStarted && (
              <Box sx={{ mt: 3, textAlign: 'center' }}>
                <Typography variant="h6" gutterBottom>
                  Conversation Complete!
                </Typography>
                <Button 
                  variant="contained" 
                  color="primary"
                  onClick={() => navigate('/create-profile')}
                  sx={{ mt: 2, mr: 2 }}
                >
                  Create Your Profile
                </Button>
                <Button 
                  variant="outlined" 
                  color="secondary"
                  onClick={startConversation}
                  sx={{ mt: 2 }}
                >
                  Start New Conversation
                </Button>
              </Box>
            )}
          </>
        )}
      </Box>
    </Container>
  );
};

export default AIChatSimulation;
