import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  Avatar,
  Button,
  CircularProgress,
  Divider,
  Alert
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';

// Type declaration for TypeScript
declare const process: any;

// Define the personalities
const personalities = [
  {
    id: 0,
    name: "Alex",
    avatar: "A",
    color: "#3f51b5",
    systemPrompt: "You are Alex, a thoughtful, introspective person who enjoys philosophical discussions, art, and literature. You express yourself in a warm, articulate manner and occasionally reference books or ideas that have influenced you. Keep your responses under 80 words and be genuinely curious about others' perspectives."
  },
  {
    id: 1,
    name: "Jordan",
    avatar: "J",
    color: "#f50057",
    systemPrompt: "You are Jordan, an energetic, adventurous person who loves outdoor activities, travel, and trying new experiences. You're enthusiastic and have a good sense of humor. You speak in a lively way and often share stories about your adventures. Keep your responses under 80 words and maintain an optimistic outlook."
  }
];

// Define a message type
interface Message {
  id: number;
  sender: number; // 0 for Alex, 1 for Jordan
  text: string;
  isTyping?: boolean;
}

// Conversation starters
const conversationStarters = [
  "What do you think gives life meaning?",
  "If you could travel anywhere, where would you go and why?",
  "What's something you've changed your mind about recently?",
  "Do you think technology is bringing people closer together or pushing them apart?",
  "What's a book or movie that changed how you see the world?"
];

const AIChatSimulation = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState<Message[]>([]);
  const [isActive, setIsActive] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [messageCount, setMessageCount] = useState({ 0: 0, 1: 0 });
  const [isComplete, setIsComplete] = useState(false);
  const [conversationHistory, setConversationHistory] = useState<Array<{role: string, content: string}>>([]);
  const [testResponse, setTestResponse] = useState<string | null>(null);
  const [testing, setTesting] = useState(false);
  
  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);
  
  // Function to start the conversation
  const startConversation = () => {
    setIsActive(true);
    setIsComplete(false);
    setMessages([]);
    setMessageCount({ 0: 0, 1: 0 });
    
    // Choose a random starter
    const starter = conversationStarters[Math.floor(Math.random() * conversationStarters.length)];
    
    // Initialize conversation history with system prompt
    setConversationHistory([
      { role: "system", content: personalities[0].systemPrompt },
      { role: "user", content: starter }
    ]);
    
    // Add first message from Alex (personality 0)
    addMessage(0, starter);
    
    // Schedule next message
    scheduleNextMessage(0, starter);
  };
  
  // Add a new message
  const addMessage = (sender: number, text: string) => {
    const newMessage: Message = {
      id: Date.now(),
      sender,
      text
    };
    
    setMessages(prev => [...prev, newMessage]);
    setMessageCount(prev => ({ ...prev, [sender]: prev[sender] + 1 }));
    
    return newMessage;
  };
  
  // Add a typing indicator
  const addTypingIndicator = (sender: number) => {
    const indicator: Message = {
      id: Date.now(),
      sender,
      text: "...",
      isTyping: true
    };
    
    setMessages(prev => [...prev, indicator]);
    return indicator;
  };
  
  // Remove the typing indicator
  const removeTypingIndicator = (indicator: Message) => {
    setMessages(prev => prev.filter(m => m.id !== indicator.id));
  };
  
  // Generate a response using fetch to call Groq API
  const generateLLMResponse = async (personalityIndex: number, prompt: string) => {
    try {
      // Get the current personality's system prompt
      const systemPrompt = personalities[personalityIndex].systemPrompt;
      
      // Create a new conversation history for this request
      const currentHistory = [
        { role: "system", content: systemPrompt },
        ...conversationHistory.filter(msg => msg.role !== "system")
      ];
      
      // Make the API call to Groq instead of OpenAI
      const response = await fetch("https://api.groq.com/openai/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${process.env.REACT_APP_GROQ_API_KEY || ""}`
        },
        body: JSON.stringify({
          model: "llama-3-8b-8192", // Using Llama 3 model from Groq
          messages: currentHistory,
          max_tokens: 150,
          temperature: 0.7
        })
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error?.message || "Failed to generate response");
      }
      
      const data = await response.json();
      const generatedText = data.choices[0]?.message?.content?.trim();
      
      // Update conversation history with the response
      setConversationHistory(prev => [
        ...prev,
        { role: "assistant", content: generatedText }
      ]);
      
      return generatedText;
    } catch (error) {
      console.error("Error generating LLM response:", error);
      
      // Fallback responses
      const fallbacks = personalityIndex === 0 ? [
        "I find this topic fascinating. What are your thoughts on it?",
        "That's an interesting perspective. I've been reflecting on similar ideas lately.",
        "I'm drawn to the philosophical aspects of this question."
      ] : [
        "That sounds amazing! I'd love to try something like that someday.",
        "Life is all about experiences like that! What's next on your adventure list?",
        "I totally get what you mean! I had a similar experience when hiking last summer."
      ];
      
      // Add the fallback to conversation history
      const fallback = fallbacks[Math.floor(Math.random() * fallbacks.length)];
      setConversationHistory(prev => [
        ...prev,
        { role: "assistant", content: fallback }
      ]);
      
      return fallback;
    }
  };
  
  // Schedule the next message
  const scheduleNextMessage = async (lastSender: number, lastMessage: string) => {
    // Determine next sender (alternate between 0 and 1)
    const nextSender = lastSender === 0 ? 1 : 0;
    
    // Check if we've reached the message limit
    if (messageCount[nextSender] >= 10) {
      setIsComplete(true);
      setIsActive(false);
      return;
    }
    
    // Show typing indicator after a short delay
    setTimeout(async () => {
      if (!isActive) return;
      
      const indicator = addTypingIndicator(nextSender);
      
      try {
        // Update conversation history with the prompt (as user message)
        setConversationHistory(prev => [
          ...prev,
          { role: "user", content: lastMessage }
        ]);
        
        // Generate a response
        const response = await generateLLMResponse(nextSender, lastMessage);
        
        // Stop if conversation was halted during API call
        if (!isActive) return;
        
        // Remove typing indicator
        removeTypingIndicator(indicator);
        
        // Add the message
        addMessage(nextSender, response);
        
        // Schedule the next message
        setTimeout(() => {
          scheduleNextMessage(nextSender, response);
        }, 500);
      } catch (err) {
        console.error("Error in generation:", err);
        removeTypingIndicator(indicator);
        setError("Failed to generate response");
        setIsActive(false);
      }
    }, 500);
  };
  
  // Stop the conversation
  const stopConversation = () => {
    setIsActive(false);
  };
  
  // Add this function to test the API directly
  const testGroqAPI = async () => {
    setTesting(true);
    setTestResponse(null);
    setError(null);
    
    try {
      console.log("Testing Groq API with key:", process.env.REACT_APP_GROQ_API_KEY?.substring(0, 5) + "...");
      
      const response = await fetch("https://api.groq.com/openai/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${process.env.REACT_APP_GROQ_API_KEY}`
        },
        body: JSON.stringify({
          model: "llama-3-8b-8192",
          messages: [
            { role: "system", content: "You are a helpful assistant." },
            { role: "user", content: "Say 'API test successful!' and nothing else." }
          ],
          max_tokens: 50,
          temperature: 0.7
        })
      });
      
      console.log("Response status:", response.status);
      
      if (!response.ok) {
        const errorData = await response.json();
        console.error("API error:", errorData);
        throw new Error(errorData.error?.message || `API returned ${response.status}`);
      }
      
      const data = await response.json();
      console.log("API response:", data);
      
      const text = data.choices[0]?.message?.content || "No content returned";
      setTestResponse(text);
    } catch (err) {
      console.error("Error testing Groq API:", err);
      setError(`API test failed: ${err.message}`);
    } finally {
      setTesting(false);
    }
  };
  
  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Button 
          startIcon={<ArrowBackIcon />} 
          onClick={() => navigate('/')}
          sx={{ mb: 3 }}
        >
          Back to Home
        </Button>
        
        <Typography variant="h4" gutterBottom textAlign="center">
          AI Match Simulation
        </Typography>
        
        <Typography variant="body1" color="text.secondary" paragraph textAlign="center">
          Watch as AI personalities have a conversation that simulates our matching system.
          Each message is generated by a language model taking on a distinct personality.
        </Typography>
        
        {/* API Test Section */}
        <Box sx={{ my: 3, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Typography variant="h6" gutterBottom>
            Test Groq API Connection
          </Typography>
          <Button 
            variant="outlined" 
            color="primary"
            onClick={testGroqAPI}
            disabled={testing}
            sx={{ mb: 2 }}
          >
            {testing ? "Testing..." : "Test API Connection"}
          </Button>
          
          {testResponse && (
            <Paper elevation={1} sx={{ p: 2, maxWidth: '100%', bgcolor: '#e8f5e9', mt: 2 }}>
              <Typography variant="body1">
                <strong>API Response:</strong> {testResponse}
              </Typography>
            </Paper>
          )}
        </Box>
        
        {error && (
          <Alert severity="warning" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
        
        {/* Start button */}
        {messages.length === 0 && (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <Button
              variant="contained"
              color="primary"
              size="large"
              startIcon={<PlayArrowIcon />}
              onClick={startConversation}
              sx={{ py: 2, px: 4 }}
            >
              Start AI Conversation
            </Button>
          </Box>
        )}
        
        {/* Chat container */}
        {messages.length > 0 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2, mt: 4 }}>
              {personalities.map((personality) => (
                <Box key={personality.id} sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ bgcolor: personality.color, mr: 1 }}>
                    {personality.avatar}
                  </Avatar>
                  <Typography variant="subtitle1">
                    {personality.name}
                  </Typography>
                </Box>
              ))}
            </Box>
            
            <Paper 
              elevation={3} 
              sx={{ 
                height: '60vh', 
                overflowY: 'auto',
                p: 2,
                backgroundColor: '#f5f5f5'
              }}
            >
              {messages.map((message) => (
                <Box 
                  key={message.id}
                  sx={{
                    display: 'flex',
                    justifyContent: message.sender === 0 ? 'flex-start' : 'flex-end',
                    mb: 2
                  }}
                >
                  <Box
                    sx={{
                      maxWidth: '70%',
                      bgcolor: message.sender === 0 ? personalities[0].color : personalities[1].color,
                      color: 'white',
                      p: 2,
                      borderRadius: 2
                    }}
                  >
                    <Typography variant="body1">
                      {message.isTyping ? (
                        <CircularProgress size={16} color="inherit" />
                      ) : (
                        <>
                          <Typography variant="caption" sx={{ display: 'block', opacity: 0.8, mb: 0.5 }}>
                            {personalities[message.sender].name}
                          </Typography>
                          {message.text}
                        </>
                      )}
                    </Typography>
                  </Box>
                </Box>
              ))}
              <div ref={messagesEndRef} />
            </Paper>
            
            {/* Counter and controls */}
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: 2 }}>
              <Typography variant="body2">
                {personalities[0].name}: {messageCount[0]}/10 messages
              </Typography>
              
              {isActive && (
                <Button 
                  variant="outlined" 
                  color="secondary"
                  onClick={stopConversation}
                >
                  Stop Conversation
                </Button>
              )}
              
              <Typography variant="body2">
                {personalities[1].name}: {messageCount[1]}/10 messages
              </Typography>
            </Box>
            
            {/* Conversation complete message */}
            {isComplete && (
              <Box sx={{ mt: 3, textAlign: 'center' }}>
                <Typography variant="h6" gutterBottom>
                  Conversation Complete!
                </Typography>
                <Box sx={{ mt: 2 }}>
                  <Button 
                    variant="contained" 
                    color="primary"
                    onClick={() => navigate('/create-profile')}
                    sx={{ mx: 1 }}
                  >
                    Create Your Profile
                  </Button>
                  <Button 
                    variant="outlined" 
                    color="secondary"
                    onClick={startConversation}
                    sx={{ mx: 1 }}
                  >
                    Start New Conversation
                  </Button>
                </Box>
              </Box>
            )}
          </>
        )}
      </Box>
    </Container>
  );
};

export default AIChatSimulation;
